import {
  Accordion,
  AccordionItem,
  AccordionItemButton,
  AccordionItemHeading,
  AccordionItemPanel,
} from "react-accessible-accordion";
const WEEK_DAYS = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday']

const Forecast = ({ data }) => {
    const dayInWeek = new Date().getDate();
    const forecastDays = WEEK_DAYS.slice(dayInWeek, WEEK_DAYS.length).concat(WEEK_DAYS.slice(0,dayInWeek)) //array.prototype.concat(): merge two or more arrays
  return (
    <>
      <label className="title">Daily</label>
      <Accordion allowZeroExpanded>
        {data.list.splice(0, 7).map((item, idx) => (
          <AccordionItem key={idx}>

            <AccordionItemHeading>
              <AccordionItemButton>
                <div className="daily-item">
                    <img alt="weather" className="icon-small" src={`http://openweathermap.org/img/wn/${item.weather[0].icon}@2x.png`}/>
                    <label className="day">{forecastDays[idx]}</label>
                    <label className="description">{item.weather}</label>
                </div>
              </AccordionItemButton>
            </AccordionItemHeading>
            <AccordionItemPanel></AccordionItemPanel>
          </AccordionItem> //react bootstrap
        ))}
      </Accordion>
    </>
  );
};

export default Forecast;
